    private void jBTSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBTSalvarActionPerformed
        if (!jTFNome.getText().equals("")) {
            Produto produto = new Produto();
            if (produtoId != -1) {
                produto.setProdutoId(produtoId);
            }
            produto.setNome(jTFNome.getText());
            produto.setPrecoVenda(jTFPrecoVenda.getText());
            produto.setPrecoCusto(jTFPrecoCusto.getText());
            produto.setUnidade(jTFUnidade.getText());
            produto.setCategoria(jTFCategoria.getText());
            ProdutoDAO produtodao = new ProdutoDAOMySQL();
            if (produtodao.salvar(produto)) {
                javax.swing.JOptionPane.showMessageDialog(null, "Produto cadastrado com suscesso!");
            } else {
                javax.swing.JOptionPane.showMessageDialog(null, "Produto não cadastrado!");
            }
        } else {
            javax.swing.JOptionPane.showMessageDialog(null, "O Nome deve ser preenchido!");
            jTFNome.requestFocus();
        }
    }//GEN-LAST:event_jBTSalvarActionPerformed

