    private void jBTExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBTExcluirActionPerformed
        String id = javax.swing.JOptionPane.showInputDialog("Digite o id a ser excluído");
        ProdutoDAO produtodao = new ProdutoDAOMySQL();
        Produto produto = produtodao.consultarPK(id);
        if (produto != null) {
            jTFNome.setText(produto.getNome());
            jTFPrecoVenda.setText(produto.getPrecoVenda());
            jTFPrecoCusto.setText(produto.getPrecoCusto());
            jTFUnidade.setText(produto.getUnidade());
            jTFCategoria.setText(produto.getCategoria());

            Object[] opcoes = {"Sim", "Não"};
            int resposta = javax.swing.JOptionPane.showOptionDialog(null, "Confirma a exclusão do Produto?", "Exclusão", 0, javax.swing.JOptionPane.QUESTION_MESSAGE, null, opcoes, opcoes[1]);
            if (resposta == 0) {
                if (produtodao.apagarPK(Integer.parseInt(id))) {
                    javax.swing.JOptionPane.showMessageDialog(null, "Produto excluído com suscesso!");
                    jTFNome.setText("");
                    jTFPrecoVenda.setText("");
                    jTFPrecoCusto.setText("");
                    jTFUnidade.setText("");
                    jTFCategoria.setText("");
                } else {
                    javax.swing.JOptionPane.showMessageDialog(null, "Produto não excluído!");
                }
            }
        }
    }//GEN-LAST:event_jBTExcluirActionPerformed

    private void JBTAlterarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_JBTAlterarActionPerformed
        String id = javax.swing.JOptionPane.showInputDialog("Digite o id a ser alterado");
        ProdutoDAO produtodao = new ProdutoDAOMySQL();
        Produto produto = produtodao.consultarPK(id);
        if (produto != null) {
            produtoId = produto.getProdutoId();
            jTFNome.setText(produto.getNome());
            jTFPrecoVenda.setText(produto.getPrecoVenda());
            jTFPrecoCusto.setText(produto.getPrecoCusto());
            jTFUnidade.setText(produto.getUnidade());
            jTFCategoria.setText(produto.getCategoria());
        }
    }//GEN-LAST:event_JBTAlterarActionPerformed

